 class ArApplicationServiceHttp extends ArApplicationService { public function start() { $data = $this->parseHttpServiceHanlder(); return $this->runService($data); } public function parseHttpServiceHanlder() { if ($ws = arPost('ws')) : if (!$ws = arComp('rpc.api')->decrypt($ws)) : throw new ArServiceException('ws query format incorrect error'); endif; if (empty($ws['class']) || empty($ws['method']) || !isset($ws['param'])) : throw new ArServiceException('ws query param missing error'); endif; return array( 'class' => $ws['class'], 'method' => $ws['method'], 'param' => $ws['param'], ); else : throw new ArServiceException('ws query ws info missing error'); endif; } protected function runService($ws = array()) { $service = $ws['class'] . 'Service'; $method = $ws['method'] . 'Worker'; $param = $ws['param']; try { $serviceHolder = new $service; $serviceHolder->init(); if (!is_callable(array($serviceHolder, $method))) : throw new ArServiceException('ws service do not hava a method ' . $method); endif; return call_user_func_array(array($serviceHolder, $method), $param); } catch(Exception $e) { throw new ArServiceException($e->getMessage()); } } } 
 class ArApplicationWeb extends ArApplication { public $route = array(); public function start() { if (AR_DEBUG) : arComp('ext.out')->deBug('[APP_WEB_START]'); endif; if (ini_get('session.auto_start') == 0) : session_start(); endif; $this->processRequest(); } public function processRequest() { $this->runController(Ar::getConfig('requestRoute')); } public function runController($route) { if (AR_DEBUG) : arComp('ext.out')->deBug('[CONTROLLER_RUN]'); endif; Ar::setConfig('requestRoute', $route); if (empty($route['a_c'])) : $c = 'Index'; else : $c = ucfirst($route['a_c']); endif; $this->route['a_c'] = $c; $class = $c . 'Controller'; if (AR_DEBUG) : arComp('ext.out')->deBug('|CONTROLLER_EXEC:'. $class .'|'); endif; if (class_exists($class)) : $this->_c = new $class; $this->_c->init(); $action = ($a = empty($route['a_a']) ? AR_DEFAULT_ACTION : $route['a_a']) . 'Action'; $this->route['a_a'] = $a; if (is_callable(array($this->_c, $action))) : try { if (AR_DEBUG) : arComp('ext.out')->deBug('|ACTION_RUN:' . $action . '|'); endif; $this->_c->$action(); } catch (ArException $e) { if (!AR_AS_OUTER_FRAME) : throw new ArException($e->getMessage()); endif; } else : if (!AR_AS_OUTER_FRAME) : throw new ArException('Action ' . $action . ' not found'); endif; endif; endif; } } 
 class ArController { protected $assign = array(); protected $layOutFile = 'NOT_INIT'; public function init() { } public function __call($name, $params) { $mName = empty($params[0]) ? arCfg('requestRoute.a_c') : $params[0]; if ($name == 'module') : return arModule($mName); elseif ($name == 'model') : $m = $mName . 'Model'; return ArModel::model($m); else : throw new ArException("class do not have a method $name"); endif; } public function assign(array $vals) { foreach ($vals as $key => $val) : if (is_array($val) && isset($this->assign[$key])) : $this->assign[$key] = array_merge($this->assign[$key], $val); else : $this->assign[$key] = $val; endif; endforeach; } public function show($ckey = '', $defaultReturn = '', $show = true) { $rt = array(); if (empty($ckey)) : $rt = $this->assign; else : if (strpos($ckey, '.') === false) : if (isset($this->assign[$ckey])) : $rt = $this->assign[$ckey]; endif; else : $cE = explode('.', $ckey); $rt = $this->assign; while ($k = array_shift($cE)) : if (empty($rt[$k])) : $rt = $defaultReturn; break; else : $rt = $rt[$k]; endif; endwhile; endif; endif; if ($show) : echo $rt; else : return $rt; endif; } protected function display($view = '', $fetch = false) { $headerFile = ''; $footerFile = ''; if ($this->layOutFile === 'NOT_INIT') : $headerFile = AR_APP_VIEW_PATH . 'Layout' . DS . 'header' . '.' . arCfg('TPL_SUFFIX'); $footerFile = AR_APP_VIEW_PATH . 'Layout' . DS . 'footer' . '.' . arCfg('TPL_SUFFIX'); elseif ($this->layOutFile) : $headerFile = $this->layOutFile . '_header' . '.' . arCfg('TPL_SUFFIX'); $footerFile = $this->layOutFile . '_footer' . '.' . arCfg('TPL_SUFFIX'); endif; if ($headerFile) : if (is_file($headerFile)) : $this->fetch($headerFile); else : if ($this->layOutFile !== 'NOT_INIT') : throw new ArException("not fount layout header file : " . $headerFile, '2000'); endif; endif; endif; $this->fetch($view, false); if ($footerFile) : if (is_file($footerFile)) : $this->fetch($footerFile); else : if ($this->layOutFile !== 'NOT_INIT') : throw new ArException("not fount layout footer file : " . $footerFile, '2000'); endif; endif; endif; } protected function fetch($view = '', $fetch = false) { if (is_file($view)) : $viewFile = $view; else : $viewPath = ''; $viewBasePath = arCfg('PATH.VIEW'); $overRide = false; $absolute = false; if (strpos($view, '@') === 0) : $overRide = true; $view = ltrim($view, '@'); endif; $r = Ar::a('ArApplicationWeb')->route; if (empty($view)) : $viewPath .= $r['a_c'] . DS . $r['a_a']; elseif(strpos($view, '/') !== false) : if (substr($view, 0, 1) == '/') : $absolute = true; $viewPath .= str_replace('/', DS, ltrim($view, '/')); else : $viewPath .= $r['a_c'] . DS . str_replace('/', DS, ltrim($view, '/')); endif; if (substr($view, -1) == '/') : $viewPath .= $r['a_a']; endif; else : $viewPath .= $r['a_c'] . DS . $view; endif; $currentC = $tempC = $r['a_c'] . 'Controller'; $preFix = ''; if (!$absolute) : while ($cP = get_parent_class($tempC)) : if (!in_array(substr($cP, 0, -10), array('Ar', 'Base'))) : $preFix = substr($cP, 0, -10) . DS . $preFix; if (!$overRide && method_exists($cP, $r['a_a'] . 'Action')) : $viewPath = str_replace(substr($tempC, 0, -10) . DS, '', $viewPath); endif; $tempC = $cP; else : break; endif; endwhile; endif; $viewFile = $viewBasePath . $preFix . $viewPath . '.' . arCfg('TPL_SUFFIX'); endif; if (is_file($viewFile)) : extract($this->assign); if ($fetch === true) : ob_start(); include $viewFile; $fetchStr = ob_get_contents(); ob_end_clean(); return $fetchStr; else : include $viewFile; endif; else : throw new ArException('view : ' . $viewFile . ' not found'); endif; } public function redirect($r = '', $show = '', $time = '0') { return arComp('url.route')->redirect($r, $show, $time, arCfg('SEG_REDIRECT_DEFAULT', 'default')); } public function redirectSuccess($r = '', $show = '', $time = '1') { return arComp('url.route')->redirect($r, '! ' . $show, $time, arCfg('SEG_REDIRECT_SUCCESS', 'success')); } public function redirectError($r = '', $show = '' , $time = '4') { return arComp('url.route')->redirect($r, '! ' . $show, $time, arCfg('SEG_REDIRECT_ERROR', 'error')); } public function showJsonSuccess($msg = ' ') { $this->showJson(array('ret_msg' => $msg, 'ret_code' => '1000', 'success' => "1")); } public function showJsonError($msg = ' ', $code = '1001') { $this->showJson(array('ret_msg' => $msg, 'ret_code' => $code, 'error_msg' => $msg, 'success' => "0")); } public function showJson($data = array(), array $options = array()) { return arComp('ext.out')->json($data, $options); } public function ifLogin() { return !!arComp('list.session')->get('uid'); } public function logOut() { arComp('list.session')->set('uid', null); } public function auth() { } public function runController($module) { $route = explode('/', $module); $requestRoute = array( 'a_m' => arCfg('requestRoute.a_m'), 'a_c' => $route[0], 'a_a' => $route[1], ); Ar::a('ArApplicationWeb')->runController($requestRoute); } public function setLayoutFile($layoutFileName = '') { if ($layoutFileName) : if (!is_file($layoutFileName)) : $layoutFileName = AR_APP_VIEW_PATH . 'Layout' . DS . $layoutFileName; endif; endif; $this->layOutFile = $layoutFileName; } } 
 class ArDbException extends ArException { } 
 class ArHttpService { protected function response($data = '') { return arComp('rpc.service')->response($data); } public function init() { } } 
 class ArModel { public $nowModel = ''; public $tableName = ''; private static $_models = array( ); static public function model($class = __CLASS__) { $key = strtolower($class); if (!isset(self::$_models[$key])) : if (AR_DEBUG) : arComp('ext.out')->deBug('|MODEL_INIT:' . $class . '|'); endif; if (strlen($class) <= 5 || substr($class, '-5') !== 'Model') : $obj = new self; $obj->tableName = strtolower($class); else : $obj = new $class; endif; $obj->nowModel = $class; if (AR_DEBUG) : arComp('ext.out')->deBug('|MODEL_START:' . $class . '|'); endif; self::$_models[$key] = $obj; else : if (AR_DEBUG) : arComp('ext.out')->deBug('|MODEL_RESTART:' . $class . '|'); endif; endif; return self::$_models[$key]; } public function upload($field, $type = 'img') { $upFile = arComp('ext.upload')->upload($field, '', $type); if (!$upFile) : arComp('list.log')->set($this->nowModel, arComp('ext.upload')->errorMsg()); return false; else : return $upFile; endif; } public function getDb($dbType = 'mysql') { return ArComp('db.' . $dbType)->table($this->tableName)->setSource($this->nowModel); } public function rules() { return array(); } public function updateCheck(array $data = array()) { $rules = $this->rules(); foreach ($rules as $key => $rule) : if (empty($rules[2]) || $rules[2] != 'update' ) : unset($rules[$key]); endif; endforeach; return $this->insertCheck($data, $rules); } public function insertCheck(array $data = array(), array $rules = array()) { $rules = empty($rules) ? $this->rules() : $rules; $r = arComp('validator.validator')->checkDataByRules($data, $rules); if (empty($r)) : return true; endif; $errorMsg = ''; foreach ($r as $errorR) : $errorMsg .= $errorR[1] . "\n"; endforeach; arComp('list.log')->set($this->nowModel, $errorMsg); return false; } public function formatData($data) { return $data; } } 
 class ArServiceException extends ArException { } 